import { useMemo } from "react";
import { View, Text } from "react-native";
import { AntDesign } from "@expo/vector-icons";
import theme from "../../styles/theme";
import todayWorkoutStyles from "../../styles/pages";

const days = ["Domingo", "Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado"];
const months = ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio", "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

const DateNavigation = () => {
    const todayDate = useMemo(() => {
        const actualDate = new Date();
        const dayName = days[actualDate.getDay()];
        const [monthDay, monthNumber] = actualDate.toLocaleDateString("en-GB").split("/");
        const monthName = months[monthNumber - 1];
        return `${dayName}, ${monthName} ${monthDay}`;
    }, []);

    return (
        <View>
            <View style={todayWorkoutStyles.dateNavigation}>
                <AntDesign name="caretleft" size={24} color={theme.fontColor1} />
                <Text style={todayWorkoutStyles.dateNavigationText}>{todayDate}</Text>
                <AntDesign name="caretright" size={24} color={theme.fontColor1}/>
            </View>
        </View>
    );
}

export default DateNavigation;
