import { useEffect, useState } from "react";
import { View, Text } from "react-native";
import { Link, useLocation } from "react-router-native";
import { menuStyle } from "../styles/components";
import theme from "../styles/theme";
import { AddToListIcon, CalendarIcon, GraphIcon, HomeIcon } from "./icons";

const sections = [
    {link: "/", element: <AddToListIcon/>},
    {link: "/calendar", element: <CalendarIcon/>},
    {link: "/statistics", element: <GraphIcon/>},
];

const Menu = () => {
    const { pathname } = useLocation();
    const [sectionActive, setSectionActive] = useState(0);

    useEffect(() => {
        let currentSectionByURL;
        sections.forEach(({ link }, index) => {
            if(pathname.indexOf(link) === 0){
                currentSectionByURL = index;
            }
        });
        setSectionActive(currentSectionByURL);
    }, [pathname]);

    return (
        <View style={menuStyle.menu}>
            <Text style={menuStyle.title}>Entrenamientos</Text>
            <View style={menuStyle.sectionContainer}>
                {sections.map(({link, element}, index) =>
                    <View
                        key={`section-${index}`}
                        style={[menuStyle.section, sectionActive === index ? menuStyle.sectionActive : null]}
                    >
                        <Link
                            to={link}
                            onPress={() => setSectionActive(index)}
                            underlayColor={theme.background1}
                        >
                            {element}
                        </Link>
                    </View>
                )}
            </View>
        </View>
    );
}


export default Menu;
