import { useMemo } from "react";
import useGlobalState from "./useGlobalState";

const days = ["Domingo", "Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado"];
const months = ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio", "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

const useDate = () => {
    const { globalState, setGlobalState } = useGlobalState();

    const detailDate = useMemo(() => {
        if(globalState.date){
            const actualDate = globalState.date;
            const dayName = days[actualDate.getDay()];
            const [monthDay, monthNumber] = actualDate.toLocaleDateString("en-GB").split("/");
            const monthName = months[monthNumber - 1];
            return `${dayName}, ${monthName} ${monthDay}`;
        }
    }, [globalState.date]);

    const setOperatedDate = (callback) => {
        // new Date(new Date().setDate(new Date().getDate() + 1));
        const actualDate = globalState.date;
        const newDay = callback(actualDate.getDate());
        console.log("newDay:",actualDate.getDate(), newDay - actualDate.getDate(), newDay);
        const miliseconds = new Date().setDate(newDay);
        console.log("miliseconds:",miliseconds);
        const newDate = new Date(miliseconds);
        console.log("newDate:",newDate);
        setGlobalState({
            ...globalState,
            date: newDate
        });
    }

    const decrementDay = () => {
        setOperatedDate(date => date - 1);
    }

    const incrementDay = () => {
        setOperatedDate(date => date + 1);
    }

    return {
        detailDate,
        decrementDay,
        incrementDay
    }
}

export default useDate;
