import { Text, View } from "react-native";
import { Link } from "react-router-native";
import { Entypo } from "@expo/vector-icons";
import theme from "../../styles/theme";
import { dayWorkoutStyles } from "../../styles/pages";
import useExercises from "../../hooks/useExercises";

const Exercises = () => {
    const { dayExercises } = useExercises();

    return (<>
        <Link to="/categories">
            <View style={dayWorkoutStyles.addExerciseButton}>
                <Entypo name="add-to-list" size={24} color={theme.fontColor1} style={dayWorkoutStyles.addExerciseButtonIcon}/>
                <Text style={dayWorkoutStyles.addExerciseButtonText}>AÃ±adir ejercicio</Text>
            </View>
        </Link>
        {dayExercises.map((exercise, index) =>
            <ExerciseCard key={`dayExercise-${index}`} {...exercise}/>
        )}
    </>);
}

const ExerciseCard = ({ name, series }) =>
    <View>
        <Text>{name}</Text>
        {series.map(({weight, reps}, index) =>
            <View key={`exercise-${name}-${index}`}>
                <Text>{weight}</Text>
                <Text>{reps}</Text>
            </View>
        )}
    </View>

export default Exercises;
