import { useMemo, useState } from "react";
import useGlobalState from "./useGlobalState";

const useExercises = () => {
    const { globalState } = useGlobalState();

    const exercises = useMemo(() => globalState?.dayExercises || [], [globalState])

    return {
        exercises
    }
}

export default useExercises;
