import { useMemo } from "react";
import { Text, TouchableOpacity } from "react-native";

const days = ["Domingo", "Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado"];

const TodayWorkout = () => {
    const todayDate = days[new Date().getDay()];

    return (<>
        <Text>{todayDate}</Text>
        <TouchableOpacity>
            <Text>Press</Text>
        </TouchableOpacity>
    </>);
}

export default TodayWorkout;
