import { useEffect, useMemo, useState } from "react";
import { months } from "../utils/date";
import useGlobalState from "./useGlobalState";

const useCalendar = () => {
    const [month, setMonth] = useState();
    const [year, setYear] = useState();
    const { globalState } = useGlobalState();
    const { date } = globalState;

    useEffect(() => {
        if(date){
            setMonth(date.getMonth());
            setYear(date.getFullYear());
        }
    }, [globalState.date]);
    
    const detailMonth = useMemo(() => `${months[date?.getMonth()]} ${year}`, [month, year]);

    const weeksMonth = useMemo(() => {
        if(!month && !year) return [];
        const amountDays = new Date(year, month + 1, 0).getDate();
        const firstDayIndexWeek = new Date(`${month + 1}/${1}/${year}`).getDay();
        const weeks = [];
        let currentWeek = Array(firstDayIndexWeek).fill("");
        for(let i=1; i<=amountDays; i++) {
            if(currentWeek.length === 7){
                weeks.push(currentWeek);
                currentWeek = [];
            }
            currentWeek.push(i);
        }
        weeks.push(currentWeek.concat(Array(7 - currentWeek.length).fill("  ")));
        return weeks;
    }, [month, year]);

    return {
        detailMonth,
        weeksMonth
    }
}

export default useCalendar;
