import { useMemo } from "react";
import { View, Text, TouchableOpacity } from "react-native";
import { AntDesign } from "@expo/vector-icons";
import todayWorkoutStyles from "../styles/today-workout";

const days = ["Domingo", "Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado"];
const months = ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio", "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];

const TodayWorkout = () => {
    return (<>
        <DateNavigation/>
        <TouchableOpacity>
            <Text>Añadir ejercicio</Text>
        </TouchableOpacity>
    </>);
}

const DateNavigation = () => {
    const todayDate = useMemo(() => {
        const actualDate = new Date();
        const dayName = days[actualDate.getDay()];
        const [monthDay, monthNumber] = actualDate.toLocaleDateString("en-GB").split("/");
        const monthName = months[monthNumber - 1];
        return `${dayName}, ${monthDay} de ${monthName}`;
    }, []);

    return (
        <View style={todayWorkoutStyles.dateNavigation}>
            <AntDesign name="caretleft" size={24} color="#ffffff" />
            <Text>{todayDate}</Text>
            <AntDesign name="caretright" size={24} color="#ffffff" />
        </View>
    );
}

export default TodayWorkout;
