import { useState } from "react";
import { Text, TouchableOpacity, View } from "react-native";
import { useParams } from "react-router-native";
import { AntDesign } from '@expo/vector-icons';
import { LabelInput } from "../components/input";
import Button from "../components/button";
import theme from "../styles/theme";
import { exerciseStyles } from "../styles/pages";
import useExercises from "../hooks/useExercises";

const defaultValues = {
    weight: 0,
    reps: 0
};

const Exercise = () => {
    const { exerciseName } = useParams();
    const [values, setValues] = useState(defaultValues);
    const { addExercise } = useExercises();

    const handleChange = (key) => (eventValue) => {
        setValues({...values, [key]: eventValue});
    }

    return (<>
        <Text style={exerciseStyles.title}>{exerciseName}</Text>
        <NumberInput
            value={values.weight}
            onChange={handleChange("weight")}
            text="Peso (kgs)"
            step={.5}
        />
        <NumberInput
            value={values.reps}
            onChange={handleChange("reps")}
            text="Repeticiones"
            step={1}
        />
        <Button text="Guardar" type="success1" onPress={addExercise(values)}/>
        <Button text="Limpiar" type="secondary1" onPress={() => setValues(defaultValues)}/>
    </>);
}

const NumberInput = ({ text, step, value, onChange, ...properties }) => {
    const changeNumber = (callback) => () => {
        const floatValue = parseFloat(value || 0);
        const newValue = callback(floatValue);
        onChange(newValue);
    }

    return (
        <View>
            <View style={exerciseStyles.numberInput}>
                <TouchableOpacity
                    onPress={changeNumber(value => value - step)}
                    style={exerciseStyles.button}
                >
                    <AntDesign name="minussquareo" size={24} color={theme.fontColor1}/>
                </TouchableOpacity>
                <LabelInput value={value} text={text} {...properties}/>
                <TouchableOpacity
                    onPress={changeNumber(value => value + step)}
                    style={exerciseStyles.button}
                >
                    <AntDesign name="plussquareo" size={24} color={theme.fontColor1}/>
                </TouchableOpacity>
            </View>
        </View>
    );
}

export default Exercise;
