import { StyleSheet } from "react-native";
import theme from "./theme";

const listItemStyle = StyleSheet.create({
    container: {
        marginVertical: 10,
    },
    listItem: {
        backgroundColor: theme.background2,
        borderRadius: 5,
        color: theme.fontColor1,
        paddingHorizontal: 15,
        paddingVertical: 10,
        fontSize: 16
    }
});

const labelInputStyle = StyleSheet.create({
    text: {
        color: theme.fontColor1,
        fontSize: 16
    },
    labelInput: {
        backgroundColor: theme.background2,
        paddingVertical: 5,
        paddingHorizontal: 10,
        marginTop: 5,
        borderRadius: 5,
        color: theme.fontColor1,
        outlineColor: theme.background2,
        marginBottom: 10
    }
});

const buttonStyle = StyleSheet.create({
    button: {
        marginVertical: 10,
        borderRadius: 5,
        paddingVertical: 5,
        paddingHorizontal: 15,
        alignItems: "center"
    },
    text: {
        color: theme.fontColor1,
        marginLeft: 10,
        fontSize: 16
    }
});

const menuStyle = StyleSheet.create({
    menu: {
        flex: 1,
        flexDirection: "row",
        borderBottomWidth: 1,
        borderBottomColor: theme.primary1,
        alignItems: "center",
        marginBottom: 10,
        padding: 5
    },
    title: {
        color: theme.fontColor1,
        fontSize: 20,
        marginLeft: 5
    }
});

export {
    listItemStyle,
    labelInputStyle,
    buttonStyle,
    menuStyle
}
