import { useRef, useState } from "react";
import { Text, TouchableOpacity, View } from "react-native";
import { useParams } from "react-router-native";
import { AntDesign } from '@expo/vector-icons';
import { LabelInput } from "../components/input";
import Button from "../components/button";
import theme from "../styles/theme";
import { exerciseStyles } from "../styles/pages";

const Exercise = () => {
    const { exerciseName = "Prueba de ejercicio" } = useParams();
    const [values, setValues] = useState({
        weight: 0,
        reps: 0
    });

    const handleChange = (key) => (event) => {
        console.log(change);
        setValues({...values, [key]: event.target.value});
    }

    return (<>
        <Text style={exerciseStyles.title}>{exerciseName}</Text>
        <NumberInput
            value={values.weight}
            onChange={handleChange("weight")}
            text="Peso (kgs)"
            step={.5}
        />
        <NumberInput
            value={values.reps}
            onChange={handleChange("reps")}
            text="Repeticiones"
            step={1}
        />
        <Button text="Guardar"/>
        <Button text="Limpiar"/>
    </>);
}

const NumberInput = ({ text, step, ...properties }) => {
    const inputRef = useRef();
    
    const increaseNumber = () => {
        const { value } = inputRef.current;
        const floatValue = parseFloat(value || 0);
        inputRef.current.value = floatValue + step;
    }

    const decreaseNumber = () => {
        const { value } = inputRef.current;
        const floatValue = parseFloat(value || 0);
        inputRef.current.value = floatValue - step;
    }

    return (
        <View>
            <View style={exerciseStyles.numberInput}>
                <TouchableOpacity onPress={decreaseNumber} style={exerciseStyles.button}>
                    <AntDesign name="minussquareo" size={24} color={theme.fontColor1}/>
                </TouchableOpacity>
                <LabelInput inputRef={inputRef} text={text} type="number" {...properties}/>
                <TouchableOpacity onPress={increaseNumber} style={exerciseStyles.button}>
                    <AntDesign name="plussquareo" size={24} color={theme.fontColor1}/>
                </TouchableOpacity>
            </View>
        </View>
    );
}

export default Exercise;
