import { useState } from "react";
import { View, Text } from "react-native";
import { Link } from "react-router-native";
import { menuStyle } from "../styles/components";
import { CalendarIcon, GraphIcon, HomeIcon } from "./icons";

const sections = [
    {link: "/", element: <HomeIcon/>},
    {link: "/calendar", element: <CalendarIcon/>},
    {link: "/statistics", element: <GraphIcon/>},
];

const Menu = () => {
    const [sectionActive, setSectionActive] = useState(0);

    return (
        <View style={menuStyle.menu}>
            <Text style={menuStyle.title}>Workouts</Text>
            <View style={menuStyle.sectionContainer}>
                {sections.map(({link, element}, index) =>
                    <View
                        key={`section-${index}`}
                        style={[menuStyle.section, sectionActive === index ? menuStyle.sectionActive : null]}
                    >
                        <Link to={link} onPress={() => setSectionActive(index)}>
                            {element}
                        </Link>
                    </View>
                )}
                <View style={[menuStyle.section, menuStyle.sectionActive]}>
                    <Link to="/">
                        <HomeIcon/>
                    </Link>
                </View>
                <View style={menuStyle.section}>
                    <Link to="/calendar">
                        <CalendarIcon/>
                    </Link>
                </View>
                <View style={menuStyle.section}>
                    <Link to="/statistics">
                        <GraphIcon/>
                    </Link>
                </View>
            </View>
        </View>
    );
}


export default Menu;
