import storage from "../utils/storage";

const getDayWorkout = () => new Promise(resolve => {
    const key = date.toLocaleDateString();
    storage.get(key).then(resolve);
});

const saveExercise = (date, exercise, weight, reps) => {
    const key = date.toLocaleDateString();
    storage.get(key).then(data => {
        if(data){
            storage.set(key, data.map(exerciseInfo => {
                if(exerciseInfo.name === exercise){
                    exerciseInfo.series.push({weight, reps});
                }
                return exerciseInfo;
            }));
            return;
        }
        storage.set(key, [{
            name: exercise,
            series: [{weight, reps}]
        }]);
    });
}

export {
    saveExercise
}
