import storage from "../utils/storage";

const getCategories = () => new Promise(resolve => {
    storage.get("categories")
        .then(res => resolve(res || []));
});

const getExercisesCategory = (category) => new Promise(resolve => {
    storage.get("exercises-categories").then(data => {
        resolve(data ? data[category] : []);
    });
});

const createCategory = categoryName => new Promise((resolve, reject) => {
    storage.get("categories").then(data => {
        const existingCategory = data.find(c => c === categoryName);
        if(!existingCategory) storage.set("categories", [...data, categoryName]).then(resolve);
        else reject();
    });
});

export {
    getCategories,
    getExercisesCategory
}
