import { useContext } from "react";
import context from "../context";

const useGlobalState = () => {
    const { globalState, setGlobalState } = useContext(context) || {};

    const startGlobalState = (onChange) => {
        (setGlobalState || onChange)({
            dayExercises: [{
                name: "exercise1",
                series: [
                    {weight: 10, reps: 10},
                    {weight: 20, reps: 20},
                ]
            }, {
                name: "exercise2",
                series: [
                    {weight: 10, reps: 10},
                    {weight: 20, reps: 20},
                ]
            }, {
                name: "exercise3",
                series: [
                    {weight: 10, reps: 10},
                    {weight: 20, reps: 20},
                ]
            }]
        });
    }

    return {
        globalState,
        setGlobalState,
        startGlobalState
    }
}

export default useGlobalState;
