import { Text, TouchableOpacity, View } from "react-native";
import { Link } from "react-router-native";
import { Entypo, MaterialIcons } from "@expo/vector-icons";
import theme from "../../styles/theme";
import { dayWorkoutStyles } from "../../styles/pages";
import useExercises from "../../hooks/useExercises";
import Table from "../../components/table";
import { AddToListIcon } from "../../components/icons";

const Exercises = () => {
    const { dayExercises } = useExercises();

    return (<>
        <AddExerciseButton/>
        {dayExercises.map((exercise, index) =>
            <ExerciseCard key={`dayExercise-${index}`} {...exercise}/>
        )}
    </>);
}

const AddExerciseButton = () =>
    <View style={dayWorkoutStyles.addExerciseButtonContainer}>
        <Link to="/categories">
            <View style={dayWorkoutStyles.addExerciseButton}>
                <AddToListIcon/>
                <Text style={dayWorkoutStyles.addExerciseButtonText}>AÃ±adir ejercicio</Text>
            </View>
        </Link>
    </View>

const ExerciseCard = ({ name, series }) => {
    const { deleteExercise } = useExercises();

    return(
        <View style={dayWorkoutStyles.exerciseCard}>
            <Link to={`/exercises/${name}`}><>
                <View style={dayWorkoutStyles.exerciseCardTitleContainer}>
                    <Text style={dayWorkoutStyles.exerciseCardTitle}>{name}</Text>
                    <TouchableOpacity onPress={deleteExercise(name)}>
                        <MaterialIcons name="delete" size={30} color={theme.fontColor1}/>
                    </TouchableOpacity>
                </View>
                <Table
                    data={series.map(({weight, reps}) => [`${weight} kgs`, `${reps} reps`])}
                />
            </></Link>
        </View>
    );
}

export default Exercises;
